import * as React from 'react';
import styled from 'styled-components'
import axios from 'axios'
import './Upload'

export type uploadFiles = {
    filename: string,
    size: number,
}

interface IUpload {
    handler?: (file: File) => void;
    path?: string;
    files?: uploadFiles[];
}

const Upload:React.FC<IUpload> = (props)=>{
    let [files,setFiles] = React.useState([])
    const {} = props
    const uploadFile = (e:any)=>{
        e.preventDefault();
        let formData = new FormData()
        const dom = document.getElementById('file') as HTMLInputElement 
        const files = dom.files as FileList
        for(let key = 0;key<files.length;key++){
            formData.append(`file[]`,files[key])
        }
        axios.post(`http://192.168.45.121:5000/upload`,formData).then(res=>{
            if(res.data.status==='success'){
                setFiles(res.data.url)
                dom.outerHTML = dom.outerHTML
            }
        })
    }
    return (
        <>
            <form method='post' action='http://192.168.45.121:5000/upload' >
                <div>
                    <input type="file" id='file' multiple />
                </div>
                <div>
                    <button type='submit' onClick={uploadFile}>Upload</button>
                </div>
                {
                    files && files.length>0?
                    (
                        <ul>
                            {
                                files.map(file=>{
                                    return <li key={file}>
                                        <a href={file} style={{color:"#2486b9",textDecoration:"none"}}>
                                            { file }
                                        </a>
                                    </li>
                                })
                            }
                        </ul>
                    ):
                    (
                        <></>
                    )
                }
            </form>
        </>
    )
}

export default Upload
